{"parsed":{"_id":"content:dev:nahj:lib:python3.12:site-packages:pip:_vendor:tenacity:__init__.py","body":"# Copyright 2016-2018 Julien Danjou\n# Copyright 2017 Elisey Zanko\n# Copyright 2016 Ã‰tienne Bersac\n# Copyright 2016 Joshua Harlow\n# Copyright 2013-2014 Ray Holder\n#\n# Licensed under the Apache License, Version 2.0 (the \"License\");\n# you may not use this file except in compliance with the License.\n# You may obtain a copy of the License at\n#\n# http://www.apache.org/licenses/LICENSE-2.0\n#\n# Unless required by applicable law or agreed to in writing, software\n# distributed under the License is distributed on an \"AS IS\" BASIS,\n# WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.\n# See the License for the specific language governing permissions and\n# limitations under the License.\n\n\nimport functools\nimport sys\nimport threading\nimport time\nimport typing as t\nimport warnings\nfrom abc import ABC, abstractmethod\nfrom concurrent import futures\nfrom inspect import iscoroutinefunction\n\n# Import all built-in retry strategies for easier usage.\nfrom .retry import retry_base  # noqa\nfrom .retry import retry_all  # noqa\nfrom .retry import retry_always  # noqa\nfrom .retry import retry_any  # noqa\nfrom .retry import retry_if_exception  # noqa\nfrom .retry import retry_if_exception_type  # noqa\nfrom .retry import retry_if_exception_cause_type  # noqa\nfrom .retry import retry_if_not_exception_type  # noqa\nfrom .retry import retry_if_not_result  # noqa\nfrom .retry import retry_if_result  # noqa\nfrom .retry import retry_never  # noqa\nfrom .retry import retry_unless_exception_type  # noqa\nfrom .retry import retry_if_exception_message  # noqa\nfrom .retry import retry_if_not_exception_message  # noqa\n\n# Import all nap strategies for easier usage.\nfrom .nap import sleep  # noqa\nfrom .nap import sleep_using_event  # noqa\n\n# Import all built-in stop strategies for easier usage.\nfrom .stop import stop_after_attempt  # noqa\nfrom .stop import stop_after_delay  # noqa\nfrom .stop import stop_all  # noqa\nfrom .stop import stop_any  # noqa\nfrom .stop import stop_never  # noqa\nfrom .stop import stop_when_event_set  # noqa\n\n# Import all built-in wait strategies for easier usage.\nfrom .wait import wait_chain  # noqa\nfrom .wait import wait_combine  # noqa\nfrom .wait import wait_exponential  # noqa\nfrom .wait import wait_fixed  # noqa\nfrom .wait import wait_incrementing  # noqa\nfrom .wait import wait_none  # noqa\nfrom .wait import wait_random  # noqa\nfrom .wait import wait_random_exponential  # noqa\nfrom .wait import wait_random_exponential as wait_full_jitter  # noqa\nfrom .wait import wait_exponential_jitter  # noqa\n\n# Import all built-in before strategies for easier usage.\nfrom .before import before_log  # noqa\nfrom .before import before_nothing  # noqa\n\n# Import all built-in after strategies for easier usage.\nfrom .after import after_log  # noqa\nfrom .after import after_nothing  # noqa\n\n# Import all built-in after strategies for easier usage.\nfrom .before_sleep import before_sleep_log  # noqa\nfrom .before_sleep import before_sleep_nothing  # noqa\n\n# Replace a conditional import with a hard-coded None so that pip does\n# not attempt to use tornado even if it is present in the environment.\n# If tornado is non-None, tenacity will attempt to execute some code\n# that is sensitive to the version of tornado, which could break pip\n# if an old version is found.\ntornado = None  # type: ignore\n\nif t.TYPE_CHECKING:\n    import types\n\n    from .retry import RetryBaseT\n    from .stop import StopBaseT\n    from .wait import WaitBaseT\n\n\nWrappedFnReturnT = t.TypeVar(\"WrappedFnReturnT\")\nWrappedFn = t.TypeVar(\"WrappedFn\", bound=t.Callable[..., t.Any])\n\n\nclass TryAgain(Exception):\n    \"\"\"Always retry the executed function when raised.\"\"\"\n\n\nNO_RESULT = object()\n\n\nclass DoAttempt:\n    pass\n\n\nclass DoSleep(float):\n    pass\n\n\nclass BaseAction:\n    \"\"\"Base class for representing actions to take by retry object.\n\n    Concrete implementations must define:\n    - __init__: to initialize all necessary fields\n    - REPR_FIELDS: class variable specifying attributes to include in repr(self)\n    - NAME: for identification in retry object methods and callbacks\n    \"\"\"\n\n    REPR_FIELDS: t.Sequence[str] = ()\n    NAME: t.Optional[str] = None\n\n    def __repr__(self) -> str:\n        state_str = \", \".join(f\"{field}={getattr(self, field)!r}\" for field in self.REPR_FIELDS)\n        return f\"{self.__class__.__name__}({state_str})\"\n\n    def __str__(self) -> str:\n        return repr(self)\n\n\nclass RetryAction(BaseAction):\n    REPR_FIELDS = (\"sleep\",)\n    NAME = \"retry\"\n\n    def __init__(self, sleep: t.SupportsFloat) -> None:\n        self.sleep = float(sleep)\n\n\n_unset = object()\n\n\ndef _first_set(first: t.Union[t.Any, object], second: t.Any) -> t.Any:\n    return second if first is _unset else first\n\n\nclass RetryError(Exception):\n    \"\"\"Encapsulates the last attempt instance right before giving up.\"\"\"\n\n    def __init__(self, last_attempt: \"Future\") -> None:\n        self.last_attempt = last_attempt\n        super().__init__(last_attempt)\n\n    def reraise(self) -> \"t.NoReturn\":\n        if self.last_attempt.failed:\n            raise self.last_attempt.result()\n        raise self\n\n    def __str__(self) -> str:\n        return f\"{self.__class__.__name__}[{self.last_attempt}]\"\n\n\nclass AttemptManager:\n    \"\"\"Manage attempt context.\"\"\"\n\n    def __init__(self, retry_state: \"RetryCallState\"):\n        self.retry_state = retry_state\n\n    def __enter__(self) -> None:\n        pass\n\n    def __exit__(\n        self,\n        exc_type: t.Optional[t.Type[BaseException]],\n        exc_value: t.Optional[BaseException],\n        traceback: t.Optional[\"types.TracebackType\"],\n    ) -> t.Optional[bool]:\n        if exc_type is not None and exc_value is not None:\n            self.retry_state.set_exception((exc_type, exc_value, traceback))\n            return True  # Swallow exception.\n        else:\n            # We don't have the result, actually.\n            self.retry_state.set_result(None)\n            return None\n\n\nclass BaseRetrying(ABC):\n    def __init__(\n        self,\n        sleep: t.Callable[[t.Union[int, float]], None] = sleep,\n        stop: \"StopBaseT\" = stop_never,\n        wait: \"WaitBaseT\" = wait_none(),\n        retry: \"RetryBaseT\" = retry_if_exception_type(),\n        before: t.Callable[[\"RetryCallState\"], None] = before_nothing,\n        after: t.Callable[[\"RetryCallState\"], None] = after_nothing,\n        before_sleep: t.Optional[t.Callable[[\"RetryCallState\"], None]] = None,\n        reraise: bool = False,\n        retry_error_cls: t.Type[RetryError] = RetryError,\n        retry_error_callback: t.Optional[t.Callable[[\"RetryCallState\"], t.Any]] = None,\n    ):\n        self.sleep = sleep\n        self.stop = stop\n        self.wait = wait\n        self.retry = retry\n        self.before = before\n        self.after = after\n        self.before_sleep = before_sleep\n        self.reraise = reraise\n        self._local = threading.local()\n        self.retry_error_cls = retry_error_cls\n        self.retry_error_callback = retry_error_callback\n\n    def copy(\n        self,\n        sleep: t.Union[t.Callable[[t.Union[int, float]], None], object] = _unset,\n        stop: t.Union[\"StopBaseT\", object] = _unset,\n        wait: t.Union[\"WaitBaseT\", object] = _unset,\n        retry: t.Union[retry_base, object] = _unset,\n        before: t.Union[t.Callable[[\"RetryCallState\"], None], object] = _unset,\n        after: t.Union[t.Callable[[\"RetryCallState\"], None], object] = _unset,\n        before_sleep: t.Union[t.Optional[t.Callable[[\"RetryCallState\"], None]], object] = _unset,\n        reraise: t.Union[bool, object] = _unset,\n        retry_error_cls: t.Union[t.Type[RetryError], object] = _unset,\n        retry_error_callback: t.Union[t.Optional[t.Callable[[\"RetryCallState\"], t.Any]], object] = _unset,\n    ) -> \"BaseRetrying\":\n        \"\"\"Copy this object with some parameters changed if needed.\"\"\"\n        return self.__class__(\n            sleep=_first_set(sleep, self.sleep),\n            stop=_first_set(stop, self.stop),\n            wait=_first_set(wait, self.wait),\n            retry=_first_set(retry, self.retry),\n            before=_first_set(before, self.before),\n            after=_first_set(after, self.after),\n            before_sleep=_first_set(before_sleep, self.before_sleep),\n            reraise=_first_set(reraise, self.reraise),\n            retry_error_cls=_first_set(retry_error_cls, self.retry_error_cls),\n            retry_error_callback=_first_set(retry_error_callback, self.retry_error_callback),\n        )\n\n    def __repr__(self) -> str:\n        return (\n            f\"<{self.__class__.__name__} object at 0x{id(self):x} (\"\n            f\"stop={self.stop}, \"\n            f\"wait={self.wait}, \"\n            f\"sleep={self.sleep}, \"\n            f\"retry={self.retry}, \"\n            f\"before={self.before}, \"\n            f\"after={self.after})>\"\n        )\n\n    @property\n    def statistics(self) -> t.Dict[str, t.Any]:\n        \"\"\"Return a dictionary of runtime statistics.\n\n        This dictionary will be empty when the controller has never been\n        ran. When it is running or has ran previously it should have (but\n        may not) have useful and/or informational keys and values when\n        running is underway and/or completed.\n\n        .. warning:: The keys in this dictionary **should** be some what\n                     stable (not changing), but there existence **may**\n                     change between major releases as new statistics are\n                     gathered or removed so before accessing keys ensure that\n                     they actually exist and handle when they do not.\n\n        .. note:: The values in this dictionary are local to the thread\n                  running call (so if multiple threads share the same retrying\n                  object - either directly or indirectly) they will each have\n                  there own view of statistics they have collected (in the\n                  future we may provide a way to aggregate the various\n                  statistics from each thread).\n        \"\"\"\n        try:\n            return self._local.statistics  # type: ignore[no-any-return]\n        except AttributeError:\n            self._local.statistics = t.cast(t.Dict[str, t.Any], {})\n            return self._local.statistics\n\n    def wraps(self, f: WrappedFn) -> WrappedFn:\n        \"\"\"Wrap a function for retrying.\n\n        :param f: A function to wraps for retrying.\n        \"\"\"\n\n        @functools.wraps(f)\n        def wrapped_f(*args: t.Any, **kw: t.Any) -> t.Any:\n            return self(f, *args, **kw)\n\n        def retry_with(*args: t.Any, **kwargs: t.Any) -> WrappedFn:\n            return self.copy(*args, **kwargs).wraps(f)\n\n        wrapped_f.retry = self  # type: ignore[attr-defined]\n        wrapped_f.retry_with = retry_with  # type: ignore[attr-defined]\n\n        return wrapped_f  # type: ignore[return-value]\n\n    def begin(self) -> None:\n        self.statistics.clear()\n        self.statistics[\"start_time\"] = time.monotonic()\n        self.statistics[\"attempt_number\"] = 1\n        self.statistics[\"idle_for\"] = 0\n\n    def iter(self, retry_state: \"RetryCallState\") -> t.Union[DoAttempt, DoSleep, t.Any]:  # noqa\n        fut = retry_state.outcome\n        if fut is None:\n            if self.before is not None:\n                self.before(retry_state)\n            return DoAttempt()\n\n        is_explicit_retry = fut.failed and isinstance(fut.exception(), TryAgain)\n        if not (is_explicit_retry or self.retry(retry_state)):\n            return fut.result()\n\n        if self.after is not None:\n            self.after(retry_state)\n\n        self.statistics[\"delay_since_first_attempt\"] = retry_state.seconds_since_start\n        if self.stop(retry_state):\n            if self.retry_error_callback:\n                return self.retry_error_callback(retry_state)\n            retry_exc = self.retry_error_cls(fut)\n            if self.reraise:\n                raise retry_exc.reraise()\n            raise retry_exc from fut.exception()\n\n        if self.wait:\n            sleep = self.wait(retry_state)\n        else:\n            sleep = 0.0\n        retry_state.next_action = RetryAction(sleep)\n        retry_state.idle_for += sleep\n        self.statistics[\"idle_for\"] += sleep\n        self.statistics[\"attempt_number\"] += 1\n\n        if self.before_sleep is not None:\n            self.before_sleep(retry_state)\n\n        return DoSleep(sleep)\n\n    def __iter__(self) -> t.Generator[AttemptManager, None, None]:\n        self.begin()\n\n        retry_state = RetryCallState(self, fn=None, args=(), kwargs={})\n        while True:\n            do = self.iter(retry_state=retry_state)\n            if isinstance(do, DoAttempt):\n                yield AttemptManager(retry_state=retry_state)\n            elif isinstance(do, DoSleep):\n                retry_state.prepare_for_next_attempt()\n                self.sleep(do)\n            else:\n                break\n\n    @abstractmethod\n    def __call__(\n        self,\n        fn: t.Callable[..., WrappedFnReturnT],\n        *args: t.Any,\n        **kwargs: t.Any,\n    ) -> WrappedFnReturnT:\n        pass\n\n\nclass Retrying(BaseRetrying):\n    \"\"\"Retrying controller.\"\"\"\n\n    def __call__(\n        self,\n        fn: t.Callable[..., WrappedFnReturnT],\n        *args: t.Any,\n        **kwargs: t.Any,\n    ) -> WrappedFnReturnT:\n        self.begin()\n\n        retry_state = RetryCallState(retry_object=self, fn=fn, args=args, kwargs=kwargs)\n        while True:\n            do = self.iter(retry_state=retry_state)\n            if isinstance(do, DoAttempt):\n                try:\n                    result = fn(*args, **kwargs)\n                except BaseException:  # noqa: B902\n                    retry_state.set_exception(sys.exc_info())  # type: ignore[arg-type]\n                else:\n                    retry_state.set_result(result)\n            elif isinstance(do, DoSleep):\n                retry_state.prepare_for_next_attempt()\n                self.sleep(do)\n            else:\n                return do  # type: ignore[no-any-return]\n\n\nif sys.version_info[1] >= 9:\n    FutureGenericT = futures.Future[t.Any]\nelse:\n    FutureGenericT = futures.Future\n\n\nclass Future(FutureGenericT):\n    \"\"\"Encapsulates a (future or past) attempted call to a target function.\"\"\"\n\n    def __init__(self, attempt_number: int) -> None:\n        super().__init__()\n        self.attempt_number = attempt_number\n\n    @property\n    def failed(self) -> bool:\n        \"\"\"Return whether a exception is being held in this future.\"\"\"\n        return self.exception() is not None\n\n    @classmethod\n    def construct(cls, attempt_number: int, value: t.Any, has_exception: bool) -> \"Future\":\n        \"\"\"Construct a new Future object.\"\"\"\n        fut = cls(attempt_number)\n        if has_exception:\n            fut.set_exception(value)\n        else:\n            fut.set_result(value)\n        return fut\n\n\nclass RetryCallState:\n    \"\"\"State related to a single call wrapped with Retrying.\"\"\"\n\n    def __init__(\n        self,\n        retry_object: BaseRetrying,\n        fn: t.Optional[WrappedFn],\n        args: t.Any,\n        kwargs: t.Any,\n    ) -> None:\n        #: Retry call start timestamp\n        self.start_time = time.monotonic()\n        #: Retry manager object\n        self.retry_object = retry_object\n        #: Function wrapped by this retry call\n        self.fn = fn\n        #: Arguments of the function wrapped by this retry call\n        self.args = args\n        #: Keyword arguments of the function wrapped by this retry call\n        self.kwargs = kwargs\n\n        #: The number of the current attempt\n        self.attempt_number: int = 1\n        #: Last outcome (result or exception) produced by the function\n        self.outcome: t.Optional[Future] = None\n        #: Timestamp of the last outcome\n        self.outcome_timestamp: t.Optional[float] = None\n        #: Time spent sleeping in retries\n        self.idle_for: float = 0.0\n        #: Next action as decided by the retry manager\n        self.next_action: t.Optional[RetryAction] = None\n\n    @property\n    def seconds_since_start(self) -> t.Optional[float]:\n        if self.outcome_timestamp is None:\n            return None\n        return self.outcome_timestamp - self.start_time\n\n    def prepare_for_next_attempt(self) -> None:\n        self.outcome = None\n        self.outcome_timestamp = None\n        self.attempt_number += 1\n        self.next_action = None\n\n    def set_result(self, val: t.Any) -> None:\n        ts = time.monotonic()\n        fut = Future(self.attempt_number)\n        fut.set_result(val)\n        self.outcome, self.outcome_timestamp = fut, ts\n\n    def set_exception(\n        self, exc_info: t.Tuple[t.Type[BaseException], BaseException, \"types.TracebackType| None\"]\n    ) -> None:\n        ts = time.monotonic()\n        fut = Future(self.attempt_number)\n        fut.set_exception(exc_info[1])\n        self.outcome, self.outcome_timestamp = fut, ts\n\n    def __repr__(self) -> str:\n        if self.outcome is None:\n            result = \"none yet\"\n        elif self.outcome.failed:\n            exception = self.outcome.exception()\n            result = f\"failed ({exception.__class__.__name__} {exception})\"\n        else:\n            result = f\"returned {self.outcome.result()}\"\n\n        slept = float(round(self.idle_for, 2))\n        clsname = self.__class__.__name__\n        return f\"<{clsname} {id(self)}: attempt #{self.attempt_number}; slept for {slept}; last result: {result}>\"\n\n\n@t.overload\ndef retry(func: WrappedFn) -> WrappedFn:\n    ...\n\n\n@t.overload\ndef retry(\n    sleep: t.Callable[[t.Union[int, float]], None] = sleep,\n    stop: \"StopBaseT\" = stop_never,\n    wait: \"WaitBaseT\" = wait_none(),\n    retry: \"RetryBaseT\" = retry_if_exception_type(),\n    before: t.Callable[[\"RetryCallState\"], None] = before_nothing,\n    after: t.Callable[[\"RetryCallState\"], None] = after_nothing,\n    before_sleep: t.Optional[t.Callable[[\"RetryCallState\"], None]] = None,\n    reraise: bool = False,\n    retry_error_cls: t.Type[\"RetryError\"] = RetryError,\n    retry_error_callback: t.Optional[t.Callable[[\"RetryCallState\"], t.Any]] = None,\n) -> t.Callable[[WrappedFn], WrappedFn]:\n    ...\n\n\ndef retry(*dargs: t.Any, **dkw: t.Any) -> t.Any:\n    \"\"\"Wrap a function with a new `Retrying` object.\n\n    :param dargs: positional arguments passed to Retrying object\n    :param dkw: keyword arguments passed to the Retrying object\n    \"\"\"\n    # support both @retry and @retry() as valid syntax\n    if len(dargs) == 1 and callable(dargs[0]):\n        return retry()(dargs[0])\n    else:\n\n        def wrap(f: WrappedFn) -> WrappedFn:\n            if isinstance(f, retry_base):\n                warnings.warn(\n                    f\"Got retry_base instance ({f.__class__.__name__}) as callable argument, \"\n                    f\"this will probably hang indefinitely (did you mean retry={f.__class__.__name__}(...)?)\"\n                )\n            r: \"BaseRetrying\"\n            if iscoroutinefunction(f):\n                r = AsyncRetrying(*dargs, **dkw)\n            elif tornado and hasattr(tornado.gen, \"is_coroutine_function\") and tornado.gen.is_coroutine_function(f):\n                r = TornadoRetrying(*dargs, **dkw)\n            else:\n                r = Retrying(*dargs, **dkw)\n\n            return r.wraps(f)\n\n        return wrap\n\n\nfrom pip._vendor.tenacity._asyncio import AsyncRetrying  # noqa:E402,I100\n\nif tornado:\n    from pip._vendor.tenacity.tornadoweb import TornadoRetrying\n\n\n__all__ = [\n    \"retry_base\",\n    \"retry_all\",\n    \"retry_always\",\n    \"retry_any\",\n    \"retry_if_exception\",\n    \"retry_if_exception_type\",\n    \"retry_if_exception_cause_type\",\n    \"retry_if_not_exception_type\",\n    \"retry_if_not_result\",\n    \"retry_if_result\",\n    \"retry_never\",\n    \"retry_unless_exception_type\",\n    \"retry_if_exception_message\",\n    \"retry_if_not_exception_message\",\n    \"sleep\",\n    \"sleep_using_event\",\n    \"stop_after_attempt\",\n    \"stop_after_delay\",\n    \"stop_all\",\n    \"stop_any\",\n    \"stop_never\",\n    \"stop_when_event_set\",\n    \"wait_chain\",\n    \"wait_combine\",\n    \"wait_exponential\",\n    \"wait_fixed\",\n    \"wait_incrementing\",\n    \"wait_none\",\n    \"wait_random\",\n    \"wait_random_exponential\",\n    \"wait_full_jitter\",\n    \"wait_exponential_jitter\",\n    \"before_log\",\n    \"before_nothing\",\n    \"after_log\",\n    \"after_nothing\",\n    \"before_sleep_log\",\n    \"before_sleep_nothing\",\n    \"retry\",\n    \"WrappedFn\",\n    \"TryAgain\",\n    \"NO_RESULT\",\n    \"DoAttempt\",\n    \"DoSleep\",\n    \"BaseAction\",\n    \"RetryAction\",\n    \"RetryError\",\n    \"AttemptManager\",\n    \"BaseRetrying\",\n    \"Retrying\",\n    \"Future\",\n    \"RetryCallState\",\n    \"AsyncRetrying\",\n]\n"},"hash":"4cWMigO2pI"}