import {
  toValue,
  useNuxtApp,
  useRequestEvent
} from "#imports";
export function useSiteConfig(options) {
  let stack;
  if (process.server)
    stack = useRequestEvent().context.siteConfig.get(options);
  else
    stack = useNuxtApp().$nuxtSiteConfig.get(options);
  Object.entries(stack).forEach(([k, v]) => {
    stack[k] = toValue(v);
  });
  return stack;
}
